        // Get references to the new HTML elements
        const mainCourseTitle = document.getElementById('main-course-title');
        const langEnBtn = document.getElementById('lang-en');
        const langHiBtn = document.getElementById('lang-hi');
        const sidebarLessonsHeading = document.getElementById('sidebar-lessons-heading');
        const lessonListUl = document.getElementById('lesson-list'); // This is now a <ul>
        const mobileDropdownLabel = document.getElementById('mobile-dropdown-label');
        const lessonDropdown = document.getElementById('lesson-dropdown');
        const lessonDisplayContainer = document.getElementById('lesson-display-area'); // This is the container for dynamic content

        let currentLanguage = 'en'; // Default language
        let activeLessonId = null; // To keep track of the currently selected lesson ID (always a sub-lesson)
        let lessonHistory = []; // Array to store viewed sub-lesson IDs for back navigation

        // Function to update button styles based on active language
        function updateLanguageButtonStyles() {
            if (currentLanguage === 'en') {
                langEnBtn.classList.add('bg-blue-500', 'text-white');
                langEnBtn.classList.remove('bg-gray-200', 'text-gray-700', 'hover:bg-gray-300');
                langHiBtn.classList.add('bg-gray-200', 'text-gray-700', 'hover:bg-gray-300');
                langHiBtn.classList.remove('bg-blue-500', 'text-white');
            } else {
                langHiBtn.classList.add('bg-blue-500', 'text-white');
                langHiBtn.classList.remove('bg-gray-200', 'text-gray-700', 'hover:bg-gray-300');
                langEnBtn.classList.add('bg-gray-200', 'text-gray-700', 'hover:bg-gray-300');
                langEnBtn.classList.remove('bg-blue-500', 'text-white');
            }
        }

        // Function to update the back button's disabled state (button is dynamic now)
        function updateBackButtonState() {
            const backBtn = document.getElementById('back-btn');
            if (backBtn) {
                backBtn.disabled = lessonHistory.length === 0;
            }
        }

        // Function to find a sub-lesson by its ID
        function findSubLesson(lessonId) {
            for (const module of ccnaModules) {
                const subLesson = module.subLessons.find(sl => sl.id === lessonId);
                if (subLesson) {
                    return subLesson;
                }
            }
            return null;
        }

        // Function to populate/re-populate the sidebar and dropdown with modules and lessons
        function populateLessonSidebar() {
            lessonListUl.innerHTML = ''; // Clear existing lessons in sidebar
            lessonDropdown.innerHTML = ''; // Clear existing options in dropdown

            // Add a default "Select Lesson" option for the dropdown
            const defaultOption = document.createElement('option');
            defaultOption.value = '';
            defaultOption.textContent = currentLanguage === 'en' ? 'Select Module/Lesson' : 'मॉड्यूल/पाठ चुनें';
            lessonDropdown.appendChild(defaultOption);

            ccnaModules.forEach(module => {
                // For sidebar (ul)
                const moduleListItem = document.createElement('li');
                moduleListItem.className = 'mb-1';

                const moduleHeading = document.createElement('div');
                moduleHeading.className = 'module-heading';
                moduleHeading.setAttribute('data-module-id', module.id);
                moduleHeading.innerHTML = `
                    <span>${module.title[currentLanguage]}</span>
                    <svg class="w-5 h-5 text-indigo-700" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5l7 7-7 7"></path>
                    </svg>
                `;
                moduleHeading.addEventListener('click', () => toggleModule(module.id));
                moduleListItem.appendChild(moduleHeading);

                const subLessonsUl = document.createElement('ul');
                subLessonsUl.className = 'sub-lessons space-y-1';
                subLessonsUl.id = `sub-lessons-${module.id}`; // Add ID for easy access

                module.subLessons.forEach(subLesson => {
                    const subLessonLi = document.createElement('li');
                    const subLessonButton = document.createElement('button');
                    subLessonButton.className = 'w-full text-left p-2 rounded-lg transition-all duration-200 ease-in-out ' +
                        'bg-gray-50 text-gray-700 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50';
                    subLessonButton.setAttribute('data-lesson-id', subLesson.id); // Data attribute for sub-lesson ID
                    subLessonButton.textContent = subLesson.title[currentLanguage];
                    subLessonButton.addEventListener('click', (event) => {
                        event.stopPropagation(); // Prevent module toggle when sub-lesson is clicked
                        displayLesson(subLesson.id);
                    });
                    subLessonLi.appendChild(subLessonButton);
                    subLessonsUl.appendChild(subLessonLi);
                });
                moduleListItem.appendChild(subLessonsUl);
                lessonListUl.appendChild(moduleListItem);

                // For dropdown (select) - Add modules as optgroups and lessons as options
                const optgroup = document.createElement('optgroup');
                optgroup.label = module.title[currentLanguage];
                module.subLessons.forEach(subLesson => {
                    const option = document.createElement('option');
                    option.value = subLesson.id;
                    option.textContent = subLesson.title[currentLanguage];
                    optgroup.appendChild(option);
                });
                lessonDropdown.appendChild(optgroup);
            });

            // Update main titles and labels
            mainCourseTitle.textContent = currentLanguage === 'en' ? 'CCNA (Cisco Certified Network Associate) Course' : 'CCNA (सिस्को सर्टिफाइड नेटवर्क एसोसिएट) कोर्स';
            sidebarLessonsHeading.textContent = currentLanguage === 'en' ? 'Modules & Lessons' : 'मॉड्यूल और पाठ';
            mobileDropdownLabel.textContent = currentLanguage === 'en' ? 'Select Module/Lesson' : 'मॉड्यूल/पाठ चुनें';

            // Re-apply active style and expansion if a lesson was previously selected
            if (activeLessonId) {
                const previouslyActiveLessonButton = document.querySelector(`[data-lesson-id="${activeLessonId}"]`);
                if (previouslyActiveLessonButton) {
                    previouslyActiveLessonButton.classList.add('bg-indigo-100', 'text-indigo-700', 'font-semibold', 'shadow-md');
                    previouslyActiveLessonButton.classList.remove('hover:bg-gray-100');

                    // Find parent module and expand it
                    const parentModuleId = ccnaModules.find(module =>
                        module.subLessons.some(sl => sl.id === activeLessonId)
                    ).id;
                    toggleModule(parentModuleId, true); // Force expand
                }
                lessonDropdown.value = activeLessonId; // Set dropdown to active lesson
            } else {
                lessonDropdown.value = ''; // Reset dropdown if no lesson is active
            }
        }

        // Function to toggle module expansion
        function toggleModule(moduleId, forceExpand = false) {
            const moduleHeading = document.querySelector(`[data-module-id="${moduleId}"]`);
            const subLessonsUl = document.getElementById(`sub-lessons-${moduleId}`);

            if (moduleHeading && subLessonsUl) {
                const isExpanded = moduleHeading.classList.contains('expanded');

                if (forceExpand && !isExpanded) {
                    moduleHeading.classList.add('expanded');
                    subLessonsUl.classList.add('expanded');
                    subLessonsUl.style.maxHeight = subLessonsUl.scrollHeight + 'px'; // Set exact height for smooth transition
                } else if (!forceExpand) { // Only toggle if not forced expand
                    if (isExpanded) {
                        moduleHeading.classList.remove('expanded');
                        subLessonsUl.style.maxHeight = '0'; // Collapse
                        subLessonsUl.classList.remove('expanded');
                    } else {
                        // Collapse all other open modules
                        document.querySelectorAll('.module-heading.expanded').forEach(otherHeading => {
                            otherHeading.classList.remove('expanded');
                            const otherUl = document.getElementById(`sub-lessons-${otherHeading.dataset.moduleId}`);
                            if (otherUl) {
                                otherUl.style.maxHeight = '0';
                                otherUl.classList.remove('expanded');
                            }
                        });

                        // Expand current module
                        moduleHeading.classList.add('expanded');
                        subLessonsUl.classList.add('expanded');
                        subLessonsUl.style.maxHeight = subLessonsUl.scrollHeight + 'px'; // Set exact height for smooth transition
                    }
                }
            }
        }


        // Function to display sub-lesson content
        function displayLesson(lessonId, fromHistory = false) {
            const lesson = findSubLesson(lessonId);
            if (!lesson) {
                displayWelcomeMessage();
                return;
            }

            // Only push to history if navigating forward (not from 'Back' button)
            // and if there's an active lesson to push, and it's not the same as the new lesson
            if (!fromHistory && activeLessonId && activeLessonId !== lessonId) {
                lessonHistory.push(activeLessonId);
            }

            activeLessonId = lessonId; // Update the active lesson ID

            // Clear previous content in the display area
            lessonDisplayContainer.innerHTML = '';

            // Create title and back button container
            const titleRowDiv = document.createElement('div');
            titleRowDiv.className = 'flex justify-between items-center mb-6 border-b-2 pb-4 border-gray-200';

            const lessonTitleH2 = document.createElement('h2');
            lessonTitleH2.className = 'text-4xl font-extrabold text-indigo-700';
            lessonTitleH2.innerHTML = lesson.title[currentLanguage];

            const backBtn = document.createElement('button');
            backBtn.id = 'back-btn'; // Assign ID for event delegation
            backBtn.className = 'px-4 py-2 rounded-lg bg-gray-300 text-gray-700 font-semibold hover:bg-gray-400 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-opacity-75 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed';
            backBtn.textContent = currentLanguage === 'en' ? 'Back' : 'पीछे';

            titleRowDiv.appendChild(lessonTitleH2);
            titleRowDiv.appendChild(backBtn);
            lessonDisplayContainer.appendChild(titleRowDiv);

            // Create lesson content div
            const lessonContentDiv = document.createElement('div');
            lessonContentDiv.className = 'text-lg text-gray-700 leading-relaxed';
            lessonContentDiv.innerHTML = lesson.content[currentLanguage];
            lessonDisplayContainer.appendChild(lessonContentDiv);

            // Update active class in sidebar for sub-lesson buttons
            document.querySelectorAll('#lesson-list button[data-lesson-id]').forEach(button => {
                button.classList.remove('bg-indigo-100', 'text-indigo-700', 'font-semibold', 'shadow-md');
                button.classList.add('hover:bg-gray-100');
            });

            const newActiveElement = document.querySelector(`[data-lesson-id="${lessonId}"]`);
            if (newActiveElement) {
                newActiveElement.classList.add('bg-indigo-100', 'text-indigo-700', 'font-semibold', 'shadow-md');
                newActiveElement.classList.remove('hover:bg-gray-100');
            }

            // Ensure the parent module is expanded
            const parentModuleId = ccnaModules.find(module =>
                module.subLessons.some(sl => sl.id === lessonId)
            ).id;
            toggleModule(parentModuleId, true); // Force expand the current module

            // Update dropdown selection
            lessonDropdown.value = lessonId;

            updateBackButtonState(); // Update back button state after displaying lesson
        }

        // Function to display the welcome message
        function displayWelcomeMessage() {
            lessonDisplayContainer.innerHTML = ''; // Clear existing content

            const welcomeDiv = document.createElement('div');
            welcomeDiv.className = 'bg-white p-8 rounded-lg shadow-lg';

            const welcomeTitle = document.createElement('h2');
            welcomeTitle.className = 'text-4xl font-extrabold text-indigo-700 mb-6 border-b-2 pb-4 border-gray-200';
            welcomeTitle.textContent = currentLanguage === 'en' ? 'Welcome to the CCNA Course!' : 'CCNA कोर्स में आपका स्वागत है!';

            const welcomeContent = document.createElement('div');
            welcomeContent.className = 'text-lg text-gray-700 leading-relaxed';
            welcomeContent.innerHTML = currentLanguage === 'en' ? `
                <p class="mb-4">
                    Select a module from the left sidebar and then choose a lesson to view its details. This course covers the fundamental knowledge required to pass the Cisco Certified Network Associate (CCNA) exam.
                </p>
                <p>
                    Each module is designed to provide a deep understanding of networking concepts and Cisco device configurations. Good luck with your studies!
                </p>
            ` : `
                <p class="mb-4">
                    बाईं ओर के साइडबार से एक मॉड्यूल का चयन करें और फिर उसके विवरण देखने के लिए एक पाठ चुनें। यह कोर्स सिस्को सर्टिफाइड नेटवर्क एसोसिएट (CCNA) परीक्षा पास करने के लिए आवश्यक मूलभूत ज्ञान को कवर करता है।
                </p>
                <p>
                    प्रत्येक मॉड्यूल नेटवर्किंग अवधारणाओं और सिस्को डिवाइस कॉन्फ़िगरेशन की गहरी समझ प्रदान करने के लिए डिज़ाइन किया गया है। आपकी पढ़ाई के लिए शुभकामनाएँ!
                </p>
            `;

            welcomeDiv.appendChild(welcomeTitle);
            welcomeDiv.appendChild(welcomeContent);
            lessonDisplayContainer.appendChild(welcomeDiv);

            activeLessonId = null; // No active lesson when welcome message is displayed
            lessonDropdown.value = ''; // Reset dropdown
            // Remove active styles from all lesson buttons
            document.querySelectorAll('#lesson-list button[data-lesson-id]').forEach(button => {
                button.classList.remove('bg-indigo-100', 'text-indigo-700', 'font-semibold', 'shadow-md');
                button.classList.add('hover:bg-gray-100');
            });
            updateBackButtonState(); // Disable back button for welcome message
        }

        // Event Listeners
        langEnBtn.addEventListener('click', () => {
            currentLanguage = 'en';
            updateLanguageButtonStyles();
            populateLessonSidebar();
            if (activeLessonId) {
                displayLesson(activeLessonId); // Re-display current lesson in new language
            } else {
                displayWelcomeMessage();
            }
        });

        langHiBtn.addEventListener('click', () => {
            currentLanguage = 'hi';
            updateLanguageButtonStyles();
            populateLessonSidebar();
            if (activeLessonId) {
                displayLesson(activeLessonId); // Re-display current lesson in new language
            } else {
                displayWelcomeMessage();
            }
        });

        lessonDropdown.addEventListener('change', (event) => {
            displayLesson(event.target.value);
        });

        // Event delegation for the back button (since it's dynamically added)
        lessonDisplayContainer.addEventListener('click', (event) => {
            if (event.target && event.target.id === 'back-btn') {
                if (lessonHistory.length > 0) {
                    const previousLessonId = lessonHistory.pop();
                    displayLesson(previousLessonId, true); // Pass true to indicate it's from history
                }
            }
        });

        // Initial setup on page load
        updateLanguageButtonStyles();
        populateLessonSidebar();
        displayWelcomeMessage(); // Display welcome message on initial load


